DROP TABLE IF EXISTS `tbl_product`;
CREATE TABLE IF NOT EXISTS `tbl_product` (
  `idProduct` int(11) NOT NULL AUTO_INCREMENT,
  `idCateProduct` int(11) DEFAULT NULL,
  `nameProduct` text COLLATE utf8mb4_unicode_ci,
  `descriptProduct` text COLLATE utf8mb4_unicode_ci,
  `brand` text COLLATE utf8mb4_unicode_ci,
  `status` int(11) DEFAULT NULL,
  `imgProd1` text COLLATE utf8mb4_unicode_ci,
  `imgProd2` text COLLATE utf8mb4_unicode_ci,
  `imgProd3` text COLLATE utf8mb4_unicode_ci,
  `imgProd4` text COLLATE utf8mb4_unicode_ci,
  `priceProduct` int(11) DEFAULT NULL,
  `special` int(11) DEFAULT NULL,
  `sale` int(11) DEFAULT NULL,
  `slug` text COLLATE utf8mb4_unicode_ci,
  PRIMARY KEY (`idProduct`),
  KEY `fk_id_cate_prod` (`idCateProduct`)
) ENGINE=InnoDB AUTO_INCREMENT=59 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
