INSERT INTO `tbl_product` (`idProduct`, `idCateProduct`, `nameProduct`, `descriptProduct`, `brand`, `status`, `imgProd1`, `imgProd2`, `imgProd3`, `imgProd4`, `priceProduct`, `special`, `sale`, `slug`) VALUES
(44, 9, 'Nhàu lá khô', 'Trái nhàu khô là loại quả đã được sơ chế của trái nhàu tươi. Hiện nay loài cây này ở nước ta chỉ được biết đến với một số cái tên khác là cây ngao, nhàu rừng hay nhàu núi. Cây nhàu có tên khoa học là Morinda Citrifolia L, đây là loài cây thuộc họ Cà phê. ', 'Cây nhàu sống tại các vùng có khí hậu nhiệt đới và ôn đới. Do đó, có thể tìm thấy loài cây này tại Châu Á, Châu Úc. Tại Châu Á thường phân bố ở Trung Quốc, Ấn Độ và Việt Nam. ', 1, 'nhàu lá khô 1.jpg', 'nhàu lá khô 2.jpg', 'nhàu lá khô 4.jpg', 'bột nhàu 0.jpg', 100000, 0, 5, 'nhau-la-kho'),
(45, 9, 'Bột nhàu', 'Bột nhàu là một chế phẩm từ những quả nhàu chín được sản xuất trong một quy trình khéo chín và vô trùng. Xét về công dụng thì bột nhàu có tác dụng tuyệt vời không kém gì so với trái nhàu tươi hoặc trái nhàu khô, không chỉ vậy nó bảo quản rất đơn giản cũng như quá trình vận chuyển không hề khó khăn. Vậy, Bột nhàu có những công dụng như thế nào và nó được sản xuất ra sao?', 'Hàm lượng bao gồm trên 110 vi dưỡng chất khác nhau (đây là một nghiên cứu không chỉ được các nhà khoa học Việt Nam mà cả những trung tâm khoa học lớn trên thế giới nghiên cứu).', 1, 'bột nhàu 0.jpg', 'botnhau1.jpg', 'botnhau2.jpg', 'botnhau3.jpg', 100000, 0, 99, 'bot-nhau'),
(46, 9, 'Nước cốt nhàu', 'Cách dùng: Ngày 2 hay 3 lần, mỗi lần 15- 30ml uống trực tiếp với nước lọc trước bữa ăn ( hoặc sau ăn với những người đau bao tử)\r\n\r\nDùng cho người trưởng thành không dùng cho phụ nữ mang thai.', '00% Nước ép Trái Nhàu.', 0, 'nuoccotnhau1.jpg', 'nuoccotnhau2.jpg', 'nuoccotnhau3.jpg', 'nuoccotnhau4.jpg', 100000, 0, 10, 'nuoc-cot-nhau'),
(47, 9, 'Phở sẵn', 'Là loại gia vị nêm sẵn đến từ thương hiệu Aji-Quick quen thuộc trong mỗi gia đình Việt Nam. Gia vị nêm sẵn nấu phở bò Aji-Quick gói 57g với chiết xuất thịt bò cùng các loại gia vị quen thuộc, giúp các bà nội trợ có thể chế biến món phở bò cho cả gia đình một cách nhanh chóng với hương vị đậm đà.', 'Xuất xứ từ các loại tinh bột, chuẩn hàng đầu Việt Nam', 1, 'phosan1.jpg', 'phosan2.jpg', 'phosan3.jpg', 'phosannguyenchat1.jpg', 10000, 0, 1, 'pho-san'),
(48, 9, 'Bánh tráng sẵn', 'Bánh tráng Đại Lộc được sản xuất tại Quảng Nam, nguyên liệu chính để làm bánh là tinh bột tráng mỏng phơi khô. Khi ăn có thể nướng giòn hoặc nhúng qua nước để cuốn bánh tráng với thịt heo luộc, là loại đặc sản xứ Quảng nổi tiếng được nhiều du khách biết đến.', 'Đặc sản người dân xứ Quảng, ăn là ghiền', 1, 'banhtrangsan.jpg', 'banhtrangsan1.jpg', 'banhtrangsan3.jpg', 'banhtrangsan4.jpg', 100000, 0, 1, 'banh-trang-san'),
(49, 9, 'Trà Gai Leo', 'Người xưa sử dụng rễ và thân Cà gai leo như một dược liệu đầu vị để giải quyết các vấn đề về gan và giải độc cơ thể', 'Cà gai leo được các nhà khoa học quan tâm và nghiên cứu từ 1980. Có 4 luận án tiến sĩ và nhiều đề tài cấp cơ sở sáng tỏ thêm thành phần hoạt chất, chất tác, hiệu quả của dược liệu Cà gai leo với các vấn đề về Gan.', 1, 'tragaileo1.jpg', 'tragaileo2.jpg', 'chưa có ảnh', 'tragaileo4.jpg', 100000, 0, 1, 'tra-gai-leo'),
(50, 9, 'Trà Gai Leo', 'Người xưa sử dụng rễ và thân Cà gai leo như một dược liệu đầu vị để giải quyết các vấn đề về gan và giải độc cơ thể', 'Cà gai leo được các nhà khoa học quan tâm và nghiên cứu từ 1980. Có 4 luận án tiến sĩ và nhiều đề tài cấp cơ sở sáng tỏ thêm thành phần hoạt chất, chất tác, hiệu quả của dược liệu Cà gai leo với các vấn đề về Gan.', 1, 'tragaileo1.jpg', 'tragaileo2.jpg', 'chưa có ảnh', 'tragaileo4.jpg', 100000, 0, 1, 'tra-gai-bo'),
(51, 9, 'Sửa chua truyền thống', 'Sữa chua không chỉ là món ăn tuyệt vời hỗ trợ tiêu hóa cho buổi sáng hoặc một lựa chọn làm đồ ăn vặt lành mạnh vào buổi chiều. Món ăn tráng miệng này còn là một trong số ít các loại thực phẩm có thể dùng kết hợp với nhiều nguyên liệu khác để biến tấu món ăn ngon miệng. Sữa chua là thực phẩm có nhiều công dụng cho sức khỏe, đặc biệt là đối với hệ tiêu hóa và làn da.', 'Tự làm', 1, 'suachuatruyenthong1.jpg', 'suachuatruyenthong2.jpg', 'suachuatraicay2.jpg', 'suachuatraicay3.jpg', 10000, 0, 12, 'sua-chua-truyen-thong'),
(52, 14, 'Sửa chua nếp cẩm', 'Sữa chua nếp cẩm là món tráng miệng được đông đảo mọi người ở nhiều lứa tuổi yêu thích vì hương vị thơm ngon, thanh mát dễ ăn, lại chứa nhiều dinh dưỡng và cực kỳ dễ làm tại nhà. Hôm nay, hãy vào bếp cùng Ocop để tìm hiểu 2 cách làm sữa chua nếp cẩm nhé!', 'Của nhà làm, tươi mát', 1, 'suachuanepcam1.jpg', 'chưa có ảnh', 'suachuanepcam3.jpg', 'suachuanepcam4.jpg', 200000, 0, 1, 'sua-chua-nep-cam'),
(53, 14, 'Sửa chua trái cây', 'Bổ sung các vitamin từ các loại hoa quả, bổ dưỡng tốt cho sức khoẻ vào những ngày hè nóng nực này', 'Nhà làm, tươi mát, ăn là ghiền', 1, 'suachuatraicay2.jpg', 'suachuatraicay3.jpg', 'suachuatruyenthong1.jpg', 'suachutraicay1.jpg', 20000, 2, 1, 'sua-chua-trai-cay'),
(54, 14, 'Trà an bàng', ' túi lọc được làm từ rễ cây ngưu bàng với nhiều lợi ích đối với sức khỏe. Nhiều nghiên cứu khoa học đã ghi nhận về lợi ích của trà ngưu bàng trong điều trị một số bệnh.', 'Những củ ngưu bàng tươi được chọn lựa để cho ra những túi trà thơm ngon nhất, giữ đậm vị ngưu bàng. Để phục vụ cho nhiều người sử dụng, chúng tôi cho ra 2 dòng sản phẩm trà: Trà ngưu bàng túi lọc – Trà ngưu bàng khô', 1, 'tranbang1.jpg', 'traanbag3.jpg', 'tranbang1.jpg', 'traanbang4.jpg', 30000, 2, 1, 'tra-an-bang'),
(55, 9, 'Bánh chưng ba Hội', 'Bằng niềm đam mê với nghề nấu bánh chưng truyền thống của gia đình, cô gái trẻ Huỳnh Thị Thu Thủy, phố Mỹ Thạch Đông, phường Tân Thạnh, Thành phố Tam Kỳ', 'Quảng Nam: Người “giữ lửa” nghề bánh chưng ngon nức tiếng', 1, 'banhchung3hoi1.jpg', 'banhchungnepcam3hoi.jpg', 'banhtetbahoi1.jpg', 'banhutro3hoi.jpg', 20000, 2, 1, 'banh-chung-ba-hoi'),
(56, 9, 'Bánh ú tro', 'Đây là loại bánh không thể thiếu để cúng ông bà tổ tiên trong ngày Tết Đoan Ngọ cũng như những đám tiệc của người Việt Nam nói chung bởi vỏ bánh dai dai cùng vị béo bùi, tan ngay trong miệng. Theo dân gian, món bánh này có vị ngọt, tính mát nên thích hợp với trẻ em, người gia đang bị nóng sốt.', 'Bánh ú lá tro (hay còn gọi là bánh tro, bánh gio) là một trong những loại bánh truyền thống chứa đựng tinh hoa văn hóa ẩm thực dân tộc. ', 1, 'banhutro3hoi.jpg', 'banhutrobahoi1.jpg', 'banhutro4.jpg', 'banhustro.jpg', 20000, 1, 1, 'banh-u-tro'),
(57, 8, 'Dầu gội thảo mọc bồ kết', 'Bổ sung Protein Đậu Hà lan mượt tóc, phục hồi hư tổn,\r\nChiết xuất đậm đặc Bồ kết và 7 thảo dược quAn toàn Cho cả mẹ bầu, mẹ bỉm sữa, trẻ nhỏ\r\n', 'Bổ sung Protein Đậu Hà lan mượt tóc, phục hồi hư tổn,Chiết xuất đậm đặc Bồ kết và 7 thảo dược quý', 1, 'z3446901527827_980e6037ff277b5df03b91280b4e9400.jpg', 'z3446901536780_5cd613618ec86c64d6c38d1bd1db5f64.jpg', 'z3446901545471_21c58294afd665cfaa45f1ff61d54e81.jpg', 'z3446901527827_980e6037ff277b5df03b91280b4e9400.jpg', 3000000, 1, 2, 'dau-goi-thao-moc-bo-ket'),
(58, 14, 'Nước ION PRO', 'Nhật Bản, là loại nước hoàn toàn không sử dụng hóa chất, mang lại vị ngọt nhẹ tự nhiên, giúp cân bằng axit - kiềm trong cơ thể, khử các chất oxy hóa, loại bỏ các gốc tự do, hỗ trợ tiêu hóa đường ruột, tăng khả năng thải các chất độc hại ra khỏi cơ thể mang lại nhiều oxy và chất dinh dượng cho các tế bào, mô.', 'Nhật bản', 1, 'z3446905856466_1b52582b2f90d1565de5392070220dca.jpg', 'z3446905882064_8eb6488d4eca3330baab721b1506d640.jpg', 'z3446905868783_32ee059b07f80e9fe0ec036dfa3bfd5e.jpg', 'z3446905856466_1b52582b2f90d1565de5392070220dca.jpg', 100000, 1, 1, 'nuoc-ion-pro');

